mapper.insert:
insert into account (member_account, member_password, member_name
  )
values (#{memberAccount,jdbcType=VARCHAR}, #{memberPassword,jdbcType=VARCHAR}, #{memberName,jdbcType=VARCHAR}
  )

mapper.update:
update account
set member_password = #{memberPassword,jdbcType=VARCHAR},
  member_name = #{memberName,jdbcType=VARCHAR}
where member_account = #{memberAccount,jdbcType=VARCHAR}

mapper.select:
select member_account, member_password, member_name
from account
where member_account = #{memberAccount,jdbcType=VARCHAR}
